import React, { Component } from 'react';
import { Text, View, StyleSheet, Button, TextInput, Image } from 'react-native';

export default class FlashCard extends Component {

  constructor(props){
    super(props);
    this.state = {lista:[{pergunta1:[{pergunta:'questão', a:'a)', b:'b)', c:'c)', d:'d)', e:'e)', correta:'e)'}], pergunta2:[{pergunta:'questão', a:'a)', b:'b)', c:'c)', d:'d)', e:'e)', correta:'a)'}]}],  


  render() {
    return(
      <View style={styles.body}>
        <View style={styles.card}>
          <Text style={styles.pergunta}>{this.state.questões[0][0]}</Text>
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  body:{
    display:'flex',
    flex:1,
    justifyContent:'center',
    alignItems:'center',
  },
  card:{
    width:1000,
    height:600,
    backgroundColor:'wheat',
    justifyContent:'center',
    alignItems:'center',
  },
  pergunta:{
    fontFamily: 'Arial',
    fontSize:25,
  }
})
}}
