import React, { Component } from 'react';
import { Checkbox, Text, View, StyleSheet, TouchableOpacity, TextInput, Image } from 'react-native';

export default class FlashCard extends Component {

  constructor(props){
    super(props);
    this.state = {resposta:'', questões:[
      {pergunta:'questão', a:'a)', b:'b)', c:'c)', d:'d)', e:'e)', correta:'e)'},
      {pergunta:'quest', a:'a)', b:'b)', c:'c)', d:'d)', e:'e)', correta:'a)'}
    ]}

    this.escolha = this.escolha.bind(this)
  }

  escolha(alternativa) {
    let s = this.state



    if (alternativa == s.questões[0].correta) {
      s.resposta = 'Correto!'
    } else {
      s.resposta = 'Errado! A resposta correta é: ' + s.questões[0].correta
    }

    this.setState(s)
  }

  render() {
    return(
      <View style={styles.body}>
        <View style={styles.card}>
          <View style={styles.pergunta}>
            <Text style={styles.t_pergunta}>{this.state.questões[0].pergunta}</Text>
          </View>
          <View style={styles.alternativas}>
            <TouchableOpacity style={styles.t_alternativa} onPress={this.escolha()}>
              <Text>{this.state.questões[0].a}</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.t_alternativa} onPress={this.escolha}>
              <Text>{this.state.questões[0].b}</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.t_alternativa} onPress={this.escolha}>
              <Text>{this.state.questões[0].c}</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.t_alternativa} onPress={this.escolha}>
              <Text>{this.state.questões[0].d}</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.t_alternativa} onPress={this.escolha}> 
              <Text>{this.state.questões[0].e}</Text>
            </TouchableOpacity>
          </View>
          <View>
            <Text>{this.state.resposta}</Text>
          </View>
        </View>
      </View>
    );
  }
}



const styles = StyleSheet.create({
  body:{
    display:'flex',
    flex:1,
    justifyContent:'center',
    alignItems:'center',
  },
  card:{
    width:1000,
    height:600,
    backgroundColor:'wheat',
    justifyContent:'center',
    alignItems:'center',
  },
  t_pergunta:{
    fontFamily: 'Arial',
    fontSize:25,
  },
  alternativas:{

  },
  t_alternativa:{
    backgroundColor:'tranparent'
  }
})
