import React, { Component } from 'react';
import { Checkbox, Text, View, StyleSheet, Button, TextInput, Image } from 'react-native';

export default class FlashCard extends Component {

  constructor(props){
    super(props);
    this.state = {questões:[
      {pergunta:'questão', a:'a)', b:'b)', c:'c)', d:'d)', e:'e)', correta:'e)'},
      {pergunta:'quest', a:'a)', b:'b)', c:'c)', d:'d)', e:'e)', correta:'a)'}
    ]}
  }

  App = () => {
    const [isSelected, setSelection] = useState(false);
  }

  render() {
    return(
      <View style={styles.body}>
        <View style={styles.card}>
          <View style={styles.pergunta}>
            <Text style={styles.t_pergunta}>{this.state.questões[0].pergunta}</Text>
          </View>
          <View style={styles.alternativas}>
            <Checkbox value={isSelected}
          onValueChange={setSelection}
          style={styles.checkbox}/><Text style={styles.t_alternativa}>{this.state.questões[0].a}</Text>
            <Checkbox value={isSelected}
          onValueChange={setSelection}
          style={styles.checkbox}/><Text style={styles.t_alternativa}>{this.state.questões[0].b}</Text>
            <Checkbox value={isSelected}
          onValueChange={setSelection}
          style={styles.checkbox}/><Text style={styles.t_alternativa}>{this.state.questões[0].c}</Text>
            <Checkbox value={isSelected}
          onValueChange={setSelection}
          style={styles.checkbox}/><Text style={styles.t_alternativa}>{this.state.questões[0].d}</Text>
            <Checkbox value={isSelected}
          onValueChange={setSelection}
          style={styles.checkbox}/><Text style={styles.t_alternativa}>{this.state.questões[0].e}</Text>
          </View>

        </View>
      </View>
    );
  }
}



const styles = StyleSheet.create({
  body:{
    display:'flex',
    flex:1,
    justifyContent:'center',
    alignItems:'center',
  },
  card:{
    width:1000,
    height:600,
    backgroundColor:'wheat',
    justifyContent:'center',
    alignItems:'center',
  },
  t_pergunta:{
    fontFamily: 'Arial',
    fontSize:25,
  },
  checkbox: {
    alignSelf: "center",
  }
})
