import React, { Component } from 'react';
import { Text, View, StyleSheet, Button, TextInput, Image } from 'react-native';

export default class FlashCard extends Component {

  constructor(props){
    super(props);
    this.state = {questões:[
      {pergunta:'questão', a:'a)', b:'b)', c:'c)', d:'d)', e:'e)', correta:'e)'},
      {pergunta:'quest', a:'a)', b:'b)', c:'c)', d:'d)', e:'e)', correta:'a)'}
    ]}
  }


  render() {
    return(
      <View style={styles.body}>
        <View style={styles.card}>
          <View style={styles.pergunta}>
            <Text style={styles.t_pergunta}>{this.state.questões[0].pergunta}</Text>
          </View>
          <View style={styles.alternativas}>
            <Button style={styles.t_alternativa} title={this.state.questões[0].a}/>
          </View>

        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  body:{
    display:'flex',
    flex:1,
    justifyContent:'center',
    alignItems:'center',
  },
  card:{
    width:1000,
    height:600,
    backgroundColor:'wheat',
    justifyContent:'center',
    alignItems:'center',
  },
  t_pergunta:{
    fontFamily: 'Arial',
    fontSize:25,
  }
})
