import React, { Component } from 'react';
import { Text, View, StyleSheet, Button, TextInput, Image } from 'react-native';

export default class FlashCard extends Component {

  constructor(props){
    super(props);
    this.state = {texto1:'Texto 1', texto2: 'Texto 2'}

    this.escrever = this.escrever.bind(this)
  }

  mudarVogais(texto) {
    let novoTexto = texto.toLowerCase();

    novoTexto = novoTexto.replace(/(a|e|o|u)/g, 'i');
    novoTexto = novoTexto.replace(/(á|à|â|ã)/g, 'i');
    novoTexto = novoTexto.replace(/(é|è|ê)/g, 'i');
    novoTexto = novoTexto.replace(/(ó|ò|ô|õ)/g, 'i');
    novoTexto = novoTexto.replace(/(ú|ù|û)/g, 'i');


    return novoTexto;
  }
  escrever(t) {
    let s = this.state;
    s.texto1 = t;
    s.texto2 = this.mudarVogais(t);

    this.setState(s);
  }

  render() {
    return(
      <View style={styles.body}>
        <View style={StyleSheet.card}>

        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  body:{
    display:'flex',
    flex:1,
    justifyContent:'center',
    alignItems:'center'
  },
  card:{
    width:500
  }
})
